import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};
}

    componentDidMount() {
        this.startAnimation()
    }

    startAnimation() {
        this.triggerLoadAnimation();
        setInterval(this.passiveAnimation, 1500);
    }

    passiveAnimation() {
        let colorPallet = ['#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76'];
        // console.warn("DEBUG colorPallet =", colorPallet)

        // d3.select("#animationSVG").remove();

        var svg = d3.select("#animationSVG")

        var data = [
                {x: 0, y: 0, x1:0, y2:0},
                {x: 0, y: 0, x1:0, y2:100},
                {x: 0, y: 0, x1:0, y2:200},
                {x: 50, y: 0, x1:0, y2:100},
                {x: 100, y: 0, x1:0, y2:50},
                {x: 150, y: 0, x1:0, y2:100},
                {x: 200, y: 0, x1:0, y2:200},
                {x: 0, y: 0, x1:0, y2:100},
                {x: 0, y: 0, x1:0, y2:0},
            ];

        let i = 0;
        function myLoop() {
          setTimeout(function() {
            // let randomNumber = Math.random();
            // let rect = data[i];
            let rect = d3.select("#rect"+i)

            // svg.append("rect")
            rect.attr("x", i*100)
                .attr("y", data[i].y2)
                .attr("width", 100)
                .attr("height", 100)
                .attr("fill", colorPallet[i])
                .style("opacity", 1)
                .transition()
                .duration(1000)
                .style("opacity", 0.6)
                .transition()
                .duration(1000)
                .style("opacity", 1)
            i++;
            if (i < data.length) {
              myLoop();
            }
          }, 300)
        }

        myLoop();
        for(let idx in data) {

            // let randomNumber = Math.random();
            // let rect = data[idx];
            // svg.append("rect")
            //     .attr("x", idx*100)
            //     .attr("y", rect.y2)
            //     .attr("width", 100)
            //     .attr("height", 100)
            //     .attr("fill", (d,i)=> colorPallet[idx])
            //     .style("opacity", randomNumber)
            //     .transition()
            //     .duration(1000)
            //     .style("opacity", 1)
            //     .transition()
            //     .duration(1000)
            //     .style("opacity", randomNumber)
        }
    }

    triggerLoadAnimation() {
        // let colorPallet = ['#191B58', '#9E4770', '#3C6997', '#5ADBFF', '#050505'];
        let colorPallet = ['#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76'];
        console.warn("DEBUG colorPallet =", colorPallet)

        d3.select("#animationSVG").remove();

        var svg = d3.select(".color-pallet")
            .append("svg")
            .attr("id", "animationSVG")
            .attr("width", 900)
            .attr("height", 300)

        var data = [
                {x: 0, y: 100, x1:0, y2:0},
                {x: 0, y: 100, x1:0, y2:100},
                {x: 0, y: 100, x1:0, y2:200},
                {x: 50, y: 100, x1:0, y2:100},
                {x: 100, y: 100, x1:0, y2:50},
                {x: 150, y: 100, x1:0, y2:100},
                {x: 200, y: 100, x1:0, y2:200},
                {x: 0, y: 100, x1:0, y2:100},
                {x: 0, y: 100, x1:0, y2:0},
            ];

        for(let idx in data) {
            let rect = data[idx];
            svg.append("rect")
                .attr("id", "rect" + idx)
                .attr("x", rect.x)
                .attr("y", rect.y)
                .attr("width", 100)
                .attr("height", 100)
                .attr("fill", (d,i)=> colorPallet[idx])
                .style("opacity", 0)
                .transition()
                .duration(2000)
                .attr("y", rect.y2)
                .style("opacity", 1)
                // .transition()
                // .duration(1500)
                // .attr("y", rect.y)
                // .style("opacity", 0)
        }
    }

    render() {
        return (
            <div className="loading-animation">
                <div className="color-pallet"></div>
            </div>
        );
    }
}


function Home(props) {
  return (
  	<div className="container-fluid">
        <LoadAnimation/>
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div style={{ 'textAlign': 'center', 'marginBottom': `300px` }}>
	    	<button className="btn btn-outline-dark">Contact Me</button>
	    </div>
    </div>
  )
}

export default Home;
