import React from 'react'
import ReactDOM from "react-dom";
import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker } from "react-google-maps";
import Autocomplete from 'react-google-autocomplete';
import Geocode from "react-geocode";
// import SearchBox from "react-google-maps/lib/components/places/SearchBox";
Geocode.setApiKey("AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I");
Geocode.enableDebug();

// searchBox
const { compose, withProps, lifecycle } = require("recompose");
const { StandaloneSearchBox } = require("react-google-maps/lib/components/places/StandaloneSearchBox");






const PlacesWithStandaloneSearchBox = compose(
  withProps({
    googleMapURL:
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I&v=3.exp&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />
  }),
  lifecycle({
    componentWillMount() {
      const refs = {};

      this.setState({
        places: [],
        onSearchBoxMounted: ref => {
          refs.searchBox = ref;
        },
        onPlacesChanged: () => {
          const places = refs.searchBox.getPlaces();

          this.setState({
            places
          });
        }
      });
    }
  }),
  withScriptjs
)(props => (
  <div data-standalone-searchbox="">
    <StandaloneSearchBox
      ref={props.onSearchBoxMounted}
      bounds={props.bounds}
      onPlacesChanged={props.onPlacesChanged}
    >
      <input
        type="text"
        placeholder="Customized your placeholder"
        style={{
          boxSizing: `border-box`,
          border: `1px solid transparent`,
          width: `240px`,
          height: `32px`,
          padding: `0 12px`,
          borderRadius: `3px`,
          boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
          fontSize: `14px`,
          outline: `none`,
          textOverflow: `ellipses`
        }}
      />
    </StandaloneSearchBox>
    <div>
      {props.places.map(
        ({ place_id, formatted_address, geometry: { location } }) => (
          <GoogleMap google={ this.props.google }
            defaultZoom='10'
            defaultCenter={{ lat: location.lat(), lng: location.lng() }}
          >
          </GoogleMap>
        )
      )}
    </div>
    <ol>
      {props.places.map(
        ({ place_id, formatted_address, geometry: { location } }) => (
          <li key={place_id}>
            {formatted_address}
            {" at "}
            ({location.lat()}, {location.lng()})
          </li>
        )
      )}
    </ol>
  </div>
));









class Map extends React.Component{
  constructor(props){
    super( props );
    this.state = {
      address: '',
      city: '',
      area: '',
      state: '',
      mapPosition: {
        lat: this.props.center.lat,
        lng: this.props.center.lng
      },
      markerPosition: {
        lat: this.props.center.lat,
        lng: this.props.center.lng
      }
    }
  }
  /**
    * Get the current address from the default map position and set those values in the state
    */
  componentDidMount() {
    // Try HTML5 geolocation.
    if (navigator.geolocation) {
      let that = this;
      navigator.geolocation.getCurrentPosition(function(position) {
        that.state.mapPosition.lat = position.coords.latitude;
        that.state.mapPosition.lng = position.coords.longitude;
        that.next()
      }, function() {
        console.warn("getCurrentPosition: Error!")
      });
    } else {
      // Browser doesn't support Geolocation
      console.warn("Browser doesn't support Geolocation")
    }


  };

  next() {
    Geocode.fromLatLng( this.state.mapPosition.lat , this.state.mapPosition.lng ).then(
      response => {
        const address = response.results[0].formatted_address,
        addressArray =  response.results[0].address_components,
        city = this.getCity( addressArray ),
        area = this.getArea( addressArray ),
        state = this.getState( addressArray );
        console.log( 'city', city, area, state );

        this.setState( {
          address: ( address ) ? address : '',
          area: ( area ) ? area : '',
          city: ( city ) ? city : '',
          state: ( state ) ? state : '',
        } )
      },
      error => {
        console.error(error);
      }
    );
  }
  /**
    * Component should only update ( meaning re-render ), when the user selects the address, or drags the pin
    *
    * @param nextProps
    * @param nextState
    * @return {boolean}
    */
  shouldComponentUpdate( nextProps, nextState ){
    if (
      this.state.markerPosition.lat !== this.props.center.lat ||
      this.state.address !== nextState.address ||
      this.state.city !== nextState.city ||
      this.state.area !== nextState.area ||
      this.state.state !== nextState.state
    ) {
      return true
    } else if ( this.props.center.lat === nextProps.center.lat ){
      return false
    }
  }
  /**
    * Get the city and set the city input value to the one selected
    *
    * @param addressArray
    * @return {string}
    */
  getCity = ( addressArray ) => {
    console.warn("getCity: addressArray", addressArray)
    let city = '';
    for( let i = 0; i < addressArray.length; i++ ) {
      if ( addressArray[ i ].types[0] && 'administrative_area_level_2' === addressArray[ i ].types[0] ) {
        city = addressArray[ i ].long_name;
        return city;
      }
    }
  };
  /**
    * Get the area and set the area input value to the one selected
    *
    * @param addressArray
    * @return {string}
    */
  getArea = ( addressArray ) => {
    let area = '';
    for( let i = 0; i < addressArray.length; i++ ) {
      if ( addressArray[ i ].types[0]  ) {
        for ( let j = 0; j < addressArray[ i ].types.length; j++ ) {
          if ( 'sublocality_level_1' === addressArray[ i ].types[j] || 'locality' === addressArray[ i ].types[j] ) {
            area = addressArray[ i ].long_name;
            return area;
          }
        }
      }
    }
  };
  /**
    * Get the address and set the address input value to the one selected
    *
    * @param addressArray
    * @return {string}
    */
  getState = ( addressArray ) => {
    let state = '';
    for( let i = 0; i < addressArray.length; i++ ) {
      for( let i = 0; i < addressArray.length; i++ ) {
        if ( addressArray[ i ].types[0] && 'administrative_area_level_1' === addressArray[ i ].types[0] ) {
          state = addressArray[ i ].long_name;
          return state;
        }
      }
    }
  };
  /**
    * And function for city,state and address input
    * @param event
    */
  onChange = ( event ) => {
    this.setState({ [event.target.name]: event.target.value });
  };
  /**
    * This Event triggers when the marker window is closed
    *
    * @param event
    */
  onInfoWindowClose = ( event ) => {
    console.warn("Infow window has been closed!!!", event)
  };
  /**
    * When the user types an address in the search box
    * @param place
    */
  onPlaceSelected = ( place ) => {
    console.warn("place =", place)
    const address = place.formatted_address,
    addressArray =  place.address_components,
    city = this.getCity( addressArray ),
    area = this.getArea( addressArray ),
    state = this.getState( addressArray ),
    latValue = place.geometry.location.lat(),
    lngValue = place.geometry.location.lng();
    // Set these values in the state.
    this.setState({
      address: ( address ) ? address : '',
      area: ( area ) ? area : '',
      city: ( city ) ? city : '',
      state: ( state ) ? state : '',
      markerPosition: {
      lat: latValue,
      lng: lngValue
    },
      mapPosition: {
        lat: latValue,
        lng: lngValue
      },
    })
  };
  /**
    * When the marker is dragged you get the lat and long using the functions available from event object.
    * Use geocode to get the address, city, area and state from the lat and lng positions.
    * And then set those values in the state.
    *
    * @param event
    */
  onMarkerDragEnd = ( event ) => {
    console.log( 'event', event );
    let newLat = event.latLng.lat(),
    newLng = event.latLng.lng(),
    addressArray = [];
    Geocode.fromLatLng( newLat , newLng ).then(
      response => {
        const address = response.results[0].formatted_address,
        addressArray =  response.results[0].address_components,
        city = this.getCity( addressArray ),
        area = this.getArea( addressArray ),
        state = this.getState( addressArray );
        this.setState( {
          address: ( address ) ? address : '',
          area: ( area ) ? area : '',
          city: ( city ) ? city : '',
          state: ( state ) ? state : ''
        } )
      },
     error => {
      console.error(error);
     }
    );
  };
  render(){
    const AsyncMap = withScriptjs(
      withGoogleMap(
        props => (
          <GoogleMap google={ this.props.google }
            defaultZoom={ this.props.zoom }
            defaultCenter={{ lat: this.state.mapPosition.lat, lng: this.state.mapPosition.lng }}
          >



            <Marker 
              google={ this.props.google }
              name={'Dolores park'}
              draggable={true}
              onDragEnd={ this.onMarkerDragEnd }
              position={{ lat: this.state.markerPosition.lat, lng: this.state.markerPosition.lng }}
            />
            <Marker />
            <InfoWindow
              onClose={ this.onInfoWindowClose }
              position={{ lat: ( this.state.markerPosition.lat + 0.0018 ), lng: this.state.markerPosition.lng }}
            >
              <div>
                <span style={{ padding: 0, margin: 0, color: 'black'}}>{ this.state.address }</span>
              </div>
            </InfoWindow>
          </GoogleMap>
        )
      )
    );
    let map;
    if( this.props.center.lat !== undefined ) {
      map = <div className="col">
        <AsyncMap
          googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I&libraries=places"
          loadingElement={
            <div style={{ height: `100%` }} />
          }
          containerElement={
            <div style={{ height: this.props.height, width: `100%` }} />
          }
          mapElement={
            <div style={{ height: `100%` }} />
          }
        />
        <div style={{ 'marginTop': `50px`, 'marginBottom': `50px` }}>
          <div className="form-group">
            <label htmlFor="">City</label>
            <input type="text" name="city" className="form-control" onChange={ this.onChange } readOnly="readOnly" value={ this.state.city }/>
          </div>
          <div className="form-group">
            <label htmlFor="">Area</label>
            <input type="text" name="area" className="form-control" onChange={ this.onChange } readOnly="readOnly" value={ this.state.area }/>
          </div>
          <div className="form-group">
            <label htmlFor="">State</label>
            <input type="text" name="state" className="form-control" onChange={ this.onChange } readOnly="readOnly" value={ this.state.state }/>
          </div>
          <div className="form-group">
            <label htmlFor="">Address</label>
            <input type="text" name="address" className="form-control" onChange={ this.onChange } readOnly="readOnly" value={ this.state.address }/>
          </div>
        </div>
        <PlacesWithStandaloneSearchBox/>
      </div>
    } else {
      map = <div style={{ height: this.props.height }} />
    }
    return( map )
  }
}
export default Map
