import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};
        this.data = [
                {x1: 0, y1: 0, x1:0, y2:100},
                {x1: 100, y1: 0, x2:100, y2:100},
                {x1: 200, y1: 0, x2:200, y2:100},
                {x1: 300, y1: 0, x2:300, y2:100},
                {x1: 400, y1: 0, x2:400, y2:100},
                {x1: 500, y1: 0, x2:500, y2:100},
                {x1: 600, y1: 0, x2:600, y2:100},
                {x1: 700, y1: 0, x2:700, y2:100},
                {x1: 800, y1: 0, x2:800, y2:100},

                {x1: 0, y1: 100, x1:0, y2:200},
                {x1: 100, y1: 100, x2:100, y2:200},
                {x1: 200, y1: 100, x2:200, y2:200},
                {x1: 300, y1: 100, x2:300, y2:200},
                {x1: 400, y1: 100, x2:400, y2:200},
                {x1: 500, y1: 100, x2:500, y2:200},
                {x1: 600, y1: 100, x2:600, y2:200},
                {x1: 700, y1: 100, x2:700, y2:200},
                {x1: 800, y1: 100, x2:800, y2:200},

                {x1: 0, y1: 200, x1:0, y2:0},
                {x1: 100, y1: 200, x2:100, y2:0},
                {x1: 200, y1: 200, x2:200, y2:0},
                {x1: 300, y1: 200, x2:300, y2:0},
                {x1: 400, y1: 200, x2:400, y2:0},
                {x1: 500, y1: 200, x2:500, y2:0},
                {x1: 600, y1: 200, x2:600, y2:0},
                {x1: 700, y1: 200, x2:700, y2:0},
                {x1: 800, y1: 200, x2:800, y2:0},
            ];
        this.colorPallet = ['#264653', '#2A9D8F', '#E9C46A', '#F4A261', '#E76F51'];
        this.squareDim = {width: 100, height: 100};
        this.numberOfDivistions = 50;
        // This binding is necessary to make `this` work in the callback
        this.passiveAnimation = this.passiveAnimation.bind(this);
    }

    componentDidMount() {
        this.startAnimation()
    }

    generateDataPoints() {
        this.data = [];
        console.warn("this.svgHeight = ", this.svgHeight)
        console.warn("this.svgHeight = ", this.svgWidth)
        this.squareDim.width = this.svgWidth/(this.numberOfDivistions/2);
        this.squareDim.height = this.svgHeight/(this.numberOfDivistions/2);

        // x axis
        for (let ix = 0; ix < this.numberOfDivistions; ix++) {
            // y axis
            for (let iy = 0; iy < this.numberOfDivistions; iy++) {
                let x = ix*this.squareDim.width;
                let y = iy*this.squareDim.height;
                let square = {
                    x1: Math.floor(Math.random() * this.svgWidth),
                    y1: Math.floor(Math.random() * this.svgHeight),
                    x2: x,
                    y2: y,
                }
                this.data.push(square)
            }

        }
    }

    passiveAnimation() {
        let that = this;
        for(let idx in this.data) {
            let random = Math.random();
            let rect = d3.select("#rect"+idx)
            rect.transition()
                .delay(500*random)
                .duration(1000)
                .style("opacity", random)
                .transition()
                .delay(500*random)
                .duration(1000)
                .style("opacity", 1)
        }
    }

    mouseoverAnimation(i) {
        console.warn("mouseoverAnimation",i)
        let random = Math.random();
        let rect = d3.select("#rect" +i)
        rect.transition()
            // .delay(500*random)
            .duration(500)
            .style("opacity", random)
            .transition()
            .delay(500*random)
            .duration(1000)
            .style("opacity", 1)
    }

    startAnimation() {
        // start passive animation
        // setInterval(this.passiveAnimation, 2000);
        let svgDiv = document.getElementById("home-animation");
        // this.svgWidth = svgDiv.clientWidth;
        // this.svgHeight = svgDiv.clientHeight;
        this.svgWidth = 500;
        this.svgHeight = 500;
        console.warn("DEBUG DEBUG startAnimation: this.width =", this.svgWidth)
        let svg = d3.select(svgDiv).append("svg")
            .attr("id", "animationSVG")
            .attr("width", this.svgWidth)
            .attr("height", this.svgHeight)

        this.generateDataPoints();
        for(let idx in this.data) {
            setTimeout(() => {  this.drawRectangle(svg, idx) }, 100);
        }
    }

    drawRectangle(svg, idx) {
        let random = Math.floor(Math.random() * this.colorPallet.length);
        let rect = this.data[idx];
        let that = this;
        svg.append("rect")
            .attr("id", "rect" + idx)
            .attr("class", "animation-rect")
            .attr("x", rect.x1)
            .attr("y", rect.y1)
            .attr("width", this.squareDim.width)
            .attr("height", this.squareDim.height)
            .attr("fill", this.colorPallet[random])
            .style("opacity", 0)
            .on("mouseover", function() { that.mouseoverAnimation(idx) })
            .transition()
            .duration(2000)
            .style("opacity", 1)
            .attr("y", rect.y2)
            .attr("x", rect.x2)
    }

    render() {
        return (
            <div id="home-animation"></div>
        );
    }
}


function Home(props) {
  return (
  	<div className="container-fluid">
        <LoadAnimation/>
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div className="contact-btn">
	    	<button className="btn btn-outline-light">Contact Me</button>
	    </div>
    </div>
  )
}

export default Home;
