// snake
let gameTicker;
let playing = false;
let count = 0;
const segmentDim = 10;
const boardWidth = 300;
const boardHeight = 300;
const segmentRadius = 5;
let foodCord = [];

let headCord = [];
let snakeBody = [];
let foodOnBoard = false;
let score = 0;
let speed = 200;
let snakeTail = [];
let snakeDirection = 'ArrowRight';
let lastSnakeDirection = '';
// bomb stuff
let bombCord = [];
let liveBomb = false;
let bombCount = 0;
let bombRadius = 4;

export function startSnakeGame() {
    console.log('startSnakeGame: Start game!!')
    initGame()
    playing = true;
}

document.addEventListener('keydown', (event) => {
  if(event.key === 'ArrowLeft' || event.key === 'ArrowUp' || event.key === 'ArrowRight' || event.key === 'ArrowDown') {
    event.preventDefault();
    if (snakeDirection === 'ArrowLeft' && event.key === 'ArrowRight') { return }
    if (snakeDirection === 'ArrowRight' && event.key === 'ArrowLeft') { return }
    if (snakeDirection === 'ArrowUp' && event.key === 'ArrowDown') { return }
    if (snakeDirection === 'ArrowDown' && event.key === 'ArrowUp') { return }
    snakeDirection = event.key;
  }
});

function initGame() {
  d3.select('#score')
      .style("display", "block")
  d3.select('#start-game')
      .style("display", "none")
  d3.select('#board').remove();
  d3.select('#end-text').remove();
  headCord = [0,0];
  snakeBody = [];
  score = 0;
  speed = 200;
  bombCount = 0
  snakeDirection = 'ArrowRight';
  snakeBody.push(headCord);

  d3.select('#game-space')
    .append('svg')
    .attr('id', 'board')
    .attr('width', boardWidth)
    .attr('height', boardHeight)
    .append('rect')
    .attr('class', 'snake head')
    .attr("id", "segment" + (snakeBody.length-1))
    .attr('width', segmentDim)
    .attr('height', segmentDim)
    .attr('x', headCord[0])
    .attr('y', headCord[1])
    .attr("ry", segmentRadius)
    .attr("rx", segmentRadius)
    .attr('fill', 'red')

  // do this with d3
  d3.select('#score-count')
    .text(score)

  spawnFood();
  gameTick();
}

function randomBoardCord() {
  let x = (Math.round(Math.random()*boardWidth/segmentDim)*segmentDim) - segmentDim;
  let y = (Math.round(Math.random()*boardHeight/segmentDim)*segmentDim) - segmentDim;
  // keep food on map
  if (x > boardWidth) { x = boardWidth - segmentDim}
  if (x < 0) { x = 0 }
  if (y > boardHeight) {y = boardHeight - segmentDim}
  if (y < 0) { y = 0 }

  return [x,y];
}

function spawnFood() {
  foodCord = randomBoardCord()

  d3.select('#board').append('rect')
    .attr("id", "food")
    .attr("width", segmentDim)
    .attr("height", segmentDim)
    .attr("x", foodCord[0])
    .attr("y", foodCord[1])
    .attr("ry", segmentRadius)
    .attr("rx", segmentRadius)
    .attr("fill", "limegreen")
    .style("opacity", 0)
    .transition()
    .duration(1000)
    .style("opacity", 1)

  foodOnBoard = true;
}

function spawnBomb() {
  bombCord = randomBoardCord();
  bombCount = 5;
  d3.select('#board').append('rect')
    .attr("id", "bomb")
    .attr("width", segmentDim)
    .attr("height", segmentDim)
    .attr("x", bombCord[0])
    .attr("y", bombCord[1])
    .attr("ry", segmentRadius)
    .attr("rx", segmentRadius)
    .attr("fill", "orange")
    .style("opacity", 0)
    .attr("stroke", "red")
    .transition()
    .duration(1000)
    .style("opacity", 1)

  d3.select('#board').append('text')
    .attr('id', 'bomb-text')
    .text(bombCount)
    .style('fill', 'black')
    .style('font-size', '65%')
    .attr("transform", "translate("+(bombCord[0]+segmentDim/4)+","+(bombCord[1]+segmentDim-1)+")");

  liveBomb = true;
}

function moveSnake(index, cord) {
  d3.select('#segment' + index)
    .attr("x", cord[0])
    .attr("y", cord[1]);

  if (+index !== snakeBody.length-1) {
    let nextIdx = index + 1;
    let oldCord = snakeBody[index];
    moveSnake(nextIdx, oldCord)
  }
  snakeBody[index] = [cord[0], cord[1]];
}

function gameLost() {
  clearTimeout(gameTicker);
  console.warn('Game over.')

  // remove old stuff and show game over
  d3.select('#food').transition()
    .duration(1000)
    .style("opacity", 0)
    .remove();

  d3.selectAll(".snake").transition()
    .duration(1000)
    .style("opacity", 0)
    .remove();

  d3.select('#bomb-text').remove();

  d3.select('#bomb')
    .transition()
    .duration(1000)
    .style("opacity", 0)
    .remove();

  d3.select('#game-space')
    .append('h3')
    .text('GAME OVER')
    .attr('id', 'end-text')
    .style("opacity", 0)
    .transition()
    .duration(1000)
    .style("opacity", 1)

  d3.select('#start-game')
    .style("display", "block")

}

function bombGoesBoom() {
  let colorScale = d3.scaleLinear()
    .domain([0, bombRadius])
    .range(['red', 'yellow']);

  let explosionArea = [];
  for(let i = 1; i <= bombRadius; i++) {
    let start = bombCord;
    for(let xDim = -i; xDim <= i; xDim++) {
      for(let yDim = -i; yDim <= i; yDim++) {
        let newX = start[0] + (xDim*segmentDim)
        let newY = start[1] + (yDim*segmentDim)
        let newCord = [newX, newY];
        if(!explosionArea.includes(newCord)) {
          explosionArea.push(newCord)
        }
      }
    }
  }

  for(let block of explosionArea) {
    // distance in block units
    let distance = distanceBetweenPoints (bombCord, block)/segmentDim

    d3.select('#board').append('rect')
      .attr("class", "boom")
      .attr("width", segmentDim)
      .attr("height", segmentDim)
      .attr("x", block[0])
      .attr("y", block[1])
      .attr("ry", segmentRadius)
      .attr("rx", segmentRadius)
      .attr("fill", colorScale(distance))
      .style("opacity", 1)
      .attr("stroke", "red")
      .transition()
      .duration(1500)
      .style("opacity", 0)
      .remove();

  }

  d3.select('#bomb-text').remove();
  d3.select('#bomb')
    .transition()
    .duration(1500)
    .style("opacity", 0)
    .remove();

  bombCount = 0;
  liveBomb = false;

  // ToDo:
  // cut snake body if body within radius
  isHeadInExplosion()

}

function distanceBetweenPoints (pointA, pointB) {
  let x0 = pointA[0];
  let x1 = pointB[0];
  let y0 = pointA[1];
  let y1 = pointB[1];
  let x = Math.pow((x1-x0), 2);
  let y = Math.pow((y1-y0), 2)
  let distance = Math.pow((x + y), 0.5);

  return distance;
}

function isHeadInExplosion() {
  let explosionXRange = [bombCord[0]-segmentDim*bombRadius, bombCord[0]+segmentDim*bombRadius];
  let explosionYRange = [bombCord[1]-segmentDim*bombRadius, bombCord[1]+segmentDim*bombRadius];
  let detectX = headCord[0] >= explosionXRange[0] && headCord[0] <= explosionXRange[1];
  let detectY = headCord[1] >= explosionYRange[0] && headCord[1] <= explosionYRange[1];
  if (detectX && detectY) {
    console.log("Ouch, killed by a bomb...")
    gameLost();
  }
}

function gameTick() {
  count++;
  if(snakeDirection === 'ArrowLeft') {
    headCord[0] = headCord[0] - segmentDim;
  }else if(snakeDirection === 'ArrowUp') {
    headCord[1] = headCord[1] - segmentDim;
  }else if(snakeDirection === 'ArrowRight') {
    headCord[0] = headCord[0] + segmentDim;
  }else if(snakeDirection === 'ArrowDown') {
    headCord[1] = headCord[1] + segmentDim;
  }
  //headCord = [0,0]
  snakeTail = [snakeBody[snakeBody.length-1][0], snakeBody[snakeBody.length-1][1]];
  moveSnake(0, headCord);

  if (foodCord.length > 0) {
    if(foodCord[0] === headCord[0] && foodCord[1] === headCord[1]) {
      console.warn('FOOD EATEN!!!')
      score++;
      if (speed > 100) {
        speed = speed-25;
      }

      d3.select("#score-count")
        .text(score)

      d3.select('#food').remove();
      foodOnBoard = false;

      snakeBody.push(snakeTail)

      d3.select('#board').append('rect')
        .attr("class", "snake")
        .attr("id", "segment" + (snakeBody.length-1))
        .attr("width", segmentDim)
        .attr("height", segmentDim)
        .attr("x", snakeTail[0])
        .attr("y", snakeTail[1])
        .attr("ry", segmentRadius)
        .attr("rx", segmentRadius)
        .attr("fill", "purple");
    }
  }

  if(count%5 === 0 && !foodOnBoard) {
    spawnFood();
  }

  // bomb tick
  if (liveBomb) {
    console.log('This bomb is ticking...', bombCount)
    // count down in seconds
    let slowCount = Math.round(1000/speed);
    if (+bombCount > 0 && count%slowCount === 1) {
      bombCount = bombCount - 1;
      d3.select('#bomb-text')
        .text(bombCount)
    } else if (bombCount <= 0){
      // boom
      console.log("Boom!")
      bombGoesBoom();
    }
  }

  // spawn bomb
  if(score > 0 && count%10 === 0 && !liveBomb) {
    spawnBomb();
  }

  // losing conditions
  if(headCord[0] >= boardWidth || headCord[0] < 0 // off the sides 
    || headCord[1] < 0 || headCord[1] >= boardHeight) { // off the top or bottom
    console.log('Snakes out of the box!')
    gameLost();
    return
  }
  for (let segment of snakeBody) {
    if (snakeBody.indexOf(segment) === 0) {
      continue
    }
    if(headCord[0] === segment[0] && headCord[1] === segment[1]) {
      console.log('You bit your own tail...')
      gameLost()
      return
    }
  }

  gameTicker = setTimeout(function() {
    gameTick()
  }, speed);
}
