import React, { Component } from 'react';
import './App.css';

//import PlacesWithStandaloneSearchBox from './SearchBox.js';
import Map from './Map';

import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker } from "react-google-maps";
// searchBox
const { compose, withProps, lifecycle } = require("recompose");
const { StandaloneSearchBox } = require("react-google-maps/lib/components/places/StandaloneSearchBox");
let currentPos = {
  lat:39.742043,
  lng:-104.991531
};
const MyMapComponent = withScriptjs(withGoogleMap((props) =>
  <GoogleMap
    defaultZoom={ 8 }
    center={props.center}
  >
  </GoogleMap>
))

// function getLocation() {
//   if (navigator.geolocation) {
//     navigator.geolocation.getCurrentPosition(showPosition);
//   } else {
//     console.warn("DEBUG DEBUG getLocation: Geolocation is not supported by this browser.")
//   }
// }

const PlacesWithStandaloneSearchBox = compose(
  withProps({
    googleMapURL:
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I&v=3.exp&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />
  }),
  lifecycle({
    componentWillMount() {
      const refs = {};
      function showPosition(position) {
        console.warn("showPosition: position =", position);

        const places = [];  
        const firstPlace = {};
        const firstPlaceLat = position.coords.latitude;
        const firstPlaceLng = position.coords.longitude;

        this.setState({
          places, firstPlace, firstPlaceLat, firstPlaceLng
        });
      };
      this.setState({
        places: [],
        firstPlace: {},
        firstPlaceLat: 39.74,
        firstPlaceLng: -104.991531,
        onSearchBoxMounted: ref => {
          refs.searchBox = ref;
          //let currentLocation = getLocation();
          if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(showPosition);
          } else {
            console.warn("DEBUG DEBUG getLocation: Geolocation is not supported by this browser.")
          }
          //console.warn("DEBUG DEBUG onSearchBoxMounted : currentLocation", currentLocation)
        },
        showPosition: (position) => {
          console.warn("showPosition: position =", position);

          const places = [];  
          const firstPlace = {};
          const firstPlaceLat = position.coords.latitude;
          const firstPlaceLng = position.coords.longitude;

          this.setState({
            places, firstPlace, firstPlaceLat, firstPlaceLng
          });
        },
        onPlacesChanged: () => {
          const places = refs.searchBox.getPlaces();      
          const firstPlace = places[0];
          const firstPlaceLat = firstPlace.geometry.location.lat()
          const firstPlaceLng = firstPlace.geometry.location.lng()
          console.warn("DEBUG DEBUG onPlacesChanged: firstPlace", firstPlace)
          this.setState({
            places, firstPlace, firstPlaceLat, firstPlaceLng
          });
        }
      });
    }
  }),
  withScriptjs
)(props => (

  <div data-standalone-searchbox="">
    <StandaloneSearchBox
      ref={props.onSearchBoxMounted}
      bounds={props.bounds}
      onPlacesChanged={props.onPlacesChanged}
    >
      <input
        type="text"
        placeholder="Find your destination"
        style={{
          boxSizing: `border-box`,
          border: `1px solid transparent`,
          width: `240px`,
          height: `32px`,
          padding: `0 12px`,
          borderRadius: `3px`,
          boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
          fontSize: `14px`,
          outline: `none`,
          textOverflow: `ellipses`
        }}
      />
    </StandaloneSearchBox>
    <MyMapComponent
      googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I&libraries=places"
      containerElement={
        <div style={{ height: `400px`, width: `100%` }} />
      }
      mapElement={
        <div style={{ height: `100%` }} />
      }    
      loadingElement={
        <div style={{ height: `100%` }} />
      }
      center={{ lat: props.firstPlaceLat, lng: props.firstPlaceLng }}
    />
  </div>

));


const AnyReactComponent = ({ text }) => <div>{text}</div>;

const containerStyles = {
  width: '100%',
};


export class MapContainer extends Component {
  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {}
  };

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    return (
      <div className="container-fluid" style={containerStyles}>
        <div className="row" style={{display: 'none'}}>
          <div className="col-2"></div>
          <div className="col-8">
            <h2> Planit - The best way to plan your next adventure. </h2>
            <p>
              Plan a trip from a, b, c and decide how long you want to spend in each spot 
              to generate an itinerary. Planit will show you transportation, lodging and 
              possible points of interest in the areas of travel including an estimated price 
              breakdown. Select the desired options and finalize your itinerary and youâ€™re on your way!
            </p>
          </div>
        </div>
        <div className="row">
          <div className="col-2"></div>
          <div className="col-8">
            <PlacesWithStandaloneSearchBox/>
          </div>  
        </div>
      </div>
    );
  }
}

export default MapContainer
