import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};
}

    componentDidMount() {
        this.startAnimation()
    }

    startAnimation() {
        this.triggerAnimation();
        setInterval(this.triggerAnimation, 3000);
    }

    triggerAnimation() {
        // let colorPallet = ['#191B58', '#9E4770', '#3C6997', '#5ADBFF', '#050505'];
        let colorPallet = ['#631D76', '#9E4770', '#631D76', '#9E4770', '#FBFBFB', '#9E4770', '#631D76', '#9E4770', '#631D76'];
        console.warn("DEBUG colorPallet =", colorPallet)

        d3.select("#animationSVG").remove();

        var svg = d3.select(".color-pallet")
            .append("svg")
            .attr("id", "animationSVG")
            .attr("width", 900)
            .attr("height", 300)

        var data = [
                {x: 0, y: 0, x1:0, y2:0},
                {x: 0, y: 0, x1:0, y2:100},
                {x: 0, y: 0, x1:0, y2:200},
                {x: 50, y: 0, x1:0, y2:100},
                {x: 100, y: 0, x1:0, y2:25},
                {x: 150, y: 0, x1:0, y2:100},
                {x: 200, y: 0, x1:0, y2:200},
                {x: 0, y: 0, x1:0, y2:100},
                {x: 0, y: 0, x1:0, y2:0},
            ];

        for(let idx in data) {
            let rect = data[idx];
            svg.append("rect")
                .attr("x", idx*100)
                .attr("y", rect.y)
                .attr("width", 100)
                .attr("height", 100)
                .attr("fill", (d,i)=> colorPallet[idx])
                .style("opacity", 0)
                .transition()
                .duration(1500)
                .attr("y", rect.y2)
                .style("opacity", 1)
                .transition()
                .duration(1500)
                .attr("y", rect.y)
                .style("opacity", 0)
        }

        // var rects = svg.selectAll("foo")
        //     .data(data)
        //     .enter()
        //     .append("rect")
        //     .attr("x", (d,i)=> i*100)
        //     .attr("y", d=> d.y)
        //     .attr("width", 100)
        //     .attr("height", 100)
        //     .attr("fill", '#FAA68C')
        //     .transition()
        //     .attr("y", 50)
        //     .duration(1000)
        //     .delay(250)
        //     .attr("fill", (d,i)=> colorPallet[i]);

        // console.warn("rects=", rects)
    }

    render() {
        return (
            <div className="loading-animation">
                <div className="color-pallet"></div>
            </div>
        );
    }
}


function Home(props) {
  return (
  	<div className="container-fluid">
        <LoadAnimation/>
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div style={{ 'textAlign': 'center', 'marginBottom': `300px` }}>
	    	<button className="btn btn-outline-dark">Contact Me</button>
	    </div>
    </div>
  )
}

export default Home;
