import React from "react"
import './App.css';
import { startSnakeGame, pauseSnakeGame } from './SnakeApp.js';


function startGame () {
	startSnakeGame();
}

function pauseGame () {
	pauseSnakeGame();
}

class PauseGame extends React.Component {
  constructor(props) {
    super(props);
    this.state = {isToggleOn: true};

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  }

  render() {
    return (
      <button onClick={this.handleClick}>
        {this.state.isToggleOn ? 'ON' : 'OFF'}
      </button>
    );
  }
}


function Snake(props) {
  return (
    <div className="container-fluid snake-container">
    	<div className="row">
	      <div className="col-sm">
	      	<h1>Snake</h1>
		      <div id="game-space">
		      	<svg id="board" width="300px" height="300px"></svg>
		      </div>
		      <div id="score">
		      	score: <span id="score-count"></span>
		      </div>
		      <div id="pause-game">
		      	<button className="btn"
		        			  onClick={pauseGame}>
		        	Pause
		        </button>

		        <PauseGame/>
		      </div>
		      <div id="start-game">
		        <button className="btn btn-primary"
		        			  onClick={startGame}>
		        	Start Game
		        </button>
		      </div>
		    </div>
	    </div>
	    <div className="col-sm">
	    	<a href='https://github.com/williamvennard/wills-page'>Checkout this project on GitHub </a>
	    </div>
    </div>
  )
}

export default Snake;
