import React from "react";
import AlertMessageTest from "./AlertMessage.js";
class AlertMessage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 10,
    };
    this.handleClick = this.clickThis.bind(this);
  }

  componentDidMount() {
    // start countdown
    this.interval = setInterval(this.countdown(), 1000);
  }

  countDown() {
    console.log("countDown: this.state.counter =", this.state.counter);
    if (this.state.counter > 0) {
      this.state.counter--;
      this.setState({ counter: this.state.counter });
    } else {
      clearInterval(this.interval);
    }
  }

  clickThis() {
    console.warn("YOOO THIS WAS CLICKED!", this);
  }

  render() {
    return (
      <div>
        <p>Self-Destruct In : {this.state.counter}</p>
        <AlertMessageTest message="hello world" />

        <button onClick={this.clickThis}>Hello Alert!</button>
      </div>
    );
  }
}
export default AlertMessage;
