import React from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import './App.css';

class List extends React.Component {
  constructor(props) {
    super(props);
  }

  render () {
    return (
      <div>
        <p>list:{ this.props.data } </p>

        <div>
          { this.state.data.map((item,i) => <li key={i}>Test</li>) }
        </div>


      </div>
    );
  }
};


// .map(function(item) {
//               return <div>{item}</div>
//             })



function NumberList(props) {
  const numbers = props.numbers;
  const listItems = numbers.map((number) =>
    <li>{number}</li>
  );
  return (
    <ul>{listItems}</ul>
  );
}

// const numbers = [1, 2, 3, 4, 5];
// ReactDOM.render(

//   document.getElementById('root')
// );



class Content extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="row">
        <div className="col-4">
          <div className="project-title"><h4>{this.props.props.title}</h4></div>
          <title> Tools Used </title>
          <NumberList numbers={this.props.props.tools} />,
        </div>
        <div className="col-8">
          <div className="project-image">
            <img src={this.props.props.image} alt="Project Thumbnail"/>
          </div>
          <div className="project-title"><h4>{this.props.props.title}</h4></div>
          <div className="project-category">{this.props.props.category}</div>
        </div>

      </div>
    );
  }
}

function Thumbnail(props) {
  // use a tag if using extenal link
  if(props.externalLink === "true") {
    return (
      <div className="project">
        <a href={props.link}>
          <Content props={props}/>
        </a>
      </div>
    );
  } else {
    return (
      <div className="project">
        <Link to={props.link}>
          <Content props={props}/>
        </Link>
      </div>
    );
  }

}

export default Thumbnail;
