import React, { Component } from 'react';
import './App.css';

//import PlacesWithStandaloneSearchBox from './SearchBox.js';
import Map from './Map';

import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker } from "react-google-maps";
// searchBox
const { compose, withProps, lifecycle } = require("recompose");
const { StandaloneSearchBox } = require("react-google-maps/lib/components/places/StandaloneSearchBox");


const MyMapComponent = withGoogleMap((props) =>
  <GoogleMap
    defaultZoom={8}
    defaultCenter={{ lat: -34.397, lng: 150.644 }}
  >
  </GoogleMap>
)

const PlacesWithStandaloneSearchBox = compose(
  withProps({
    googleMapURL:
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I&v=3.exp&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />
  }),
  lifecycle({
    componentWillMount() {
      const refs = {};
      this.setState({
        places: [],
        firstPlace: {},
        onSearchBoxMounted: ref => {
          refs.searchBox = ref;
        },
        onPlacesChanged: () => {
          const places = refs.searchBox.getPlaces();      
          const firstPlace = places[0];

          console.warn("DEBUG DEBUG onPlacesChanged: this.firstPlace", this.firstPlace)
          this.setState({
            places, firstPlace
          });
        }
      });
    }
  }),
  withScriptjs
)(props => (

  <div data-standalone-searchbox="">
    <StandaloneSearchBox
      ref={props.onSearchBoxMounted}
      bounds={props.bounds}
      onPlacesChanged={props.onPlacesChanged}
    >
      <input
        type="text"
        placeholder="Find your destination"
        style={{
          boxSizing: `border-box`,
          border: `1px solid transparent`,
          width: `240px`,
          height: `32px`,
          padding: `0 12px`,
          borderRadius: `3px`,
          boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
          fontSize: `14px`,
          outline: `none`,
          textOverflow: `ellipses`
        }}
      />
    </StandaloneSearchBox>
    <ol>
      {props.places.map(
        ({ place_id, formatted_address, geometry: { location } }) => (
          <li key={place_id}>
            {formatted_address}
            {" at "}
            ({location.lat()}, {location.lng()})
          </li>
        )
      )}
    </ol>
    <div>
      <h1>First place search:</h1>
      <p>{ props.firstPlace.formatted_address }</p>
    </div>

    <GoogleMap
      defaultZoom={8}
      defaultCenter={{ lat: -34.397, lng: 150.644 }}
    >
    </GoogleMap>

  </div>



));


const AnyReactComponent = ({ text }) => <div>{text}</div>;

class SimpleMap extends Component {
  static defaultProps = {
    center: {
      lat: 59.95,
      lng: 30.33
    },
    zoom: 11
  };

  render() {
    const AsyncMap = withScriptjs(
      withGoogleMap(
        props => (
          <div>

            <GoogleMap google={ this.props.google }
              defaultZoom={ 10 }
              defaultCenter={{ lat: 0, lng: 0 }}
            >
            </GoogleMap>

          </div>
        )
      )
    );
    let map;
    if( this.props.center.lat !== undefined ) {
      map = <div className="col">
        <AsyncMap
          googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyAb__W3DvdpBLKSoN6AuMzNZtcrcurvK0I&libraries=places"
          loadingElement={
            <div style={{ height: `100%` }} />
          }
          containerElement={
            <div style={{ height: this.props.height, width: `100%` }} />
          }
          mapElement={
            <div style={{ height: `100%` }} />
          }
        />

      </div>
    } else {
      map = <div style={{ height: this.props.height }} />
    }
    return( map )

  }
}



const containerStyles = {
  width: '100%',
};
let currentPos = {
  lat:39.742043,
  lng:-104.991531
};

export class MapContainer extends Component {
  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {}
  };

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    return (
      <div className="container-fluid" style={containerStyles}>
        <div className="row">
          <div className="col-2"></div>
          <div className="col-8">
            <h2> Planit - The best way to plan your next adventure. </h2>
            <p>
              Plan a trip from a, b, c and decide how long you want to spend in each spot 
              to generate an itinerary. Planit will show you transportation, lodging and 
              possible points of interest in the areas of travel including an estimated price 
              breakdown. Select the desired options and finalize your itinerary and youâ€™re on your way!
            </p>
            <Map
             google={this.props.google}
             center={{lat: currentPos.lat, lng: currentPos.lng}} 
             height='300px'
             zoom={10}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-8">
            <h1>Search Box!</h1>
            <PlacesWithStandaloneSearchBox/>
          </div>  
        </div>
      </div>
    );
  }
}

export default MapContainer
