import React from "react"
import {Link} from 'react-router-dom'; // Import the Link component
import './App.css';


class CopyEmail extends React.Component {
  constructor(props) {
    super(props);
    // this.state = {isPaused: true};
    this.email = props.email;
    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {

    navigator.clipboard.writeText(this.email)
    alert("Copied the text: " + this.email);
  }

  render() {
    return (
      <div>
        <p id="email"
           onClick={this.handleClick}>
          {this.email}
        </p>
      </div>
    );
  }
}


function Contact(props) {
  return (
    <div className="container-fluid contact-container">
      <div style={{ padding: `0.5em 1em 0em`, background:`#264653`, 'borderBottom': `solid 2px #201b24` }}
           className="row">
        <h2>Get In Touch</h2>
      </div>
        <div className="row">
            <div className="col-6 text-container">




            <p>
              Interested in working together or simply have questions?
              Feel free to contact me via email, phone or social media.
            </p>
          <div className="col-4">

            <p>
              William Vennard
            </p>
            <p>
              <CopyEmail email="william.vennard@colorado.edu"/>

            </p>
            <p>
              (608) 770 - 6478
            </p>
          </div>




          <div className="col-8">
            Click <a href="/wvResume2020.pdf">here</a> to view my resume.
          </div>
            </div>
        <div className="col-6">
          <img id="my-photo" src={pictureOfMe} alt="its me"/>
        </div>
        </div>
    </div>
  )
}

export default Contact;
