import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};
}

    componentDidMount() {
        this.startAnimation()
    }

    startAnimation() {
        this.triggerLoadAnimation();
        setInterval(this.passiveAnimation, 1500);
    }

    passiveAnimation() {
        let colorPallet = ['#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76'];
        // console.warn("DEBUG colorPallet =", colorPallet)

        // d3.select("#animationSVG").remove();

        var svg = d3.select("#animationSVG")

        var data = [
                {x1: 0, y1: 100, x1:0, y2:0},
                {x1: 100, y1: 100, x2:100, y2:100},
                {x1: 200, y1: 100, x2:200, y2:200},
                {x1: 300, y1: 100, x2:300, y2:100},
                {x1: 400, y1: 100, x2:400, y2:50},
                {x1: 500, y1: 100, x2:500, y2:100},
                {x1: 600, y1: 100, x2:600, y2:200},
                {x1: 700, y1: 100, x2:700, y2:100},
                {x1: 800, y1: 100, x2:800, y2:0},
            ];

        let i = 0;
        function myLoop() {
            setTimeout(function() {
                // let randomNumber = Math.random();
                // let rect = data[i];
                let rect = d3.select("#rect"+i)

                // svg.append("rect")
                rect.attr("width", 100)
                    .attr("height", 100)
                    .attr("fill", colorPallet[i])
                    .style("opacity", 1)
                    .transition()
                    .duration(800)
                    .style("opacity", 0.6)
                    .transition()
                    .duration(1000)
                    .style("opacity", 1)
                i++;
                if (i < data.length) {
                    myLoop();
                }
            }, 300)
        }

        myLoop();
    }

    triggerLoadAnimation() {
        // let colorPallet = ['#191B58', '#9E4770', '#3C6997', '#5ADBFF', '#050505'];
        let colorPallet = ['#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76', '#9E4770', '#631D76'];
        console.warn("DEBUG colorPallet =", colorPallet)

        d3.select("#animationSVG").remove();

        var svg = d3.select(".color-pallet")
            .append("svg")
            .attr("id", "animationSVG")
            .attr("width", 900)
            .attr("height", 300)

        var data = [
                {x1: 0, y1: 100, x1:0, y2:0},
                {x1: 100, y1: 100, x2:100, y2:100},
                {x1: 200, y1: 100, x2:200, y2:200},
                {x1: 300, y1: 100, x2:300, y2:100},
                {x1: 400, y1: 100, x2:400, y2:50},
                {x1: 500, y1: 100, x2:500, y2:100},
                {x1: 600, y1: 100, x2:600, y2:200},
                {x1: 700, y1: 100, x2:700, y2:100},
                {x1: 800, y1: 100, x2:800, y2:0},
            ];

        for(let idx in data) {
            let rect = data[idx];
            svg.append("rect")
                .attr("id", "rect" + idx)
                .attr("x", rect.x1)
                .attr("y", rect.y1)
                .attr("width", 100)
                .attr("height", 100)
                .attr("fill", (d,i)=> colorPallet[idx])
                .style("opacity", 0)
                .transition()
                .duration(500)
                .style("opacity", 1)
                .transition()
                .duration(1000)
                .attr("y", rect.y2)
                .attr("x", rect.x2)
                // .transition()
                // .duration(1500)
                // .attr("y", rect.y)
                // .style("opacity", 0)
        }
    }

    render() {
        return (
            <div className="loading-animation">
                <div className="color-pallet"></div>
            </div>
        );
    }
}


function Home(props) {
  return (
  	<div className="container-fluid">
        <LoadAnimation/>
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div style={{ 'textAlign': 'center', 'marginBottom': `300px` }}>
	    	<button className="btn btn-outline-light">Contact Me</button>
	    </div>
    </div>
  )
}

export default Home;
