import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};
        this.data = [
                {x1: 0, y1: 0, x1:0, y2:100},
                {x1: 100, y1: 0, x2:100, y2:100},
                {x1: 200, y1: 0, x2:200, y2:100},
                {x1: 300, y1: 0, x2:300, y2:100},
                {x1: 400, y1: 0, x2:400, y2:100},
                {x1: 500, y1: 0, x2:500, y2:100},
                {x1: 600, y1: 0, x2:600, y2:100},
                {x1: 700, y1: 0, x2:700, y2:100},
                {x1: 800, y1: 0, x2:800, y2:100},

                {x1: 0, y1: 100, x1:0, y2:200},
                {x1: 100, y1: 100, x2:100, y2:200},
                {x1: 200, y1: 100, x2:200, y2:200},
                {x1: 300, y1: 100, x2:300, y2:200},
                {x1: 400, y1: 100, x2:400, y2:200},
                {x1: 500, y1: 100, x2:500, y2:200},
                {x1: 600, y1: 100, x2:600, y2:200},
                {x1: 700, y1: 100, x2:700, y2:200},
                {x1: 800, y1: 100, x2:800, y2:200},

                {x1: 0, y1: 200, x1:0, y2:0},
                {x1: 100, y1: 200, x2:100, y2:0},
                {x1: 200, y1: 200, x2:200, y2:0},
                {x1: 300, y1: 200, x2:300, y2:0},
                {x1: 400, y1: 200, x2:400, y2:0},
                {x1: 500, y1: 200, x2:500, y2:0},
                {x1: 600, y1: 200, x2:600, y2:0},
                {x1: 700, y1: 200, x2:700, y2:0},
                {x1: 800, y1: 200, x2:800, y2:0},
            ];
        this.colorPallet = ['#264653', '#2A9D8F', '#E9C46A', '#F4A261', '#E76F51'];
        this.squareDims = {width: 100, height: 100};
        this.svgDims =
        // This binding is necessary to make `this` work in the callback
        this.passiveAnimation = this.passiveAnimation.bind(this);
}

    componentDidMount() {
        this.startAnimation()
    }

    startAnimation() {
        this.triggerLoadAnimation();
        setInterval(this.passiveAnimation, 1500);
    }

    passiveAnimation() {
        var svg = d3.select("#animationSVG")
        let i = 0;
        let that = this;
        console.warn("this = ", this)
        let random = Math.floor(Math.random() * that.colorPallet.length);
        let rects = d3.selectAll("rect")
        rects.transition()
            .delay(200*random)
            .duration(800)
            .style("opacity", 0.6)
            .attr("fill", that.colorPallet[random])
            .transition()
            .duration(1000)
            .style("opacity", 1)

        // function myLoop(data) {
        //     setTimeout(function() {
        //         // let rect = d3.select("#rect"+i)

        //         i++;
        //         if (i < that.data.length) {
        //             myLoop();
        //         }
        //     }, 300)
        // }

        // myLoop(this.data);
    }

    triggerLoadAnimation() {
        let svgWidth = this.squareDims.width*this.data.length;
        let svgHeight = this.squareDims.height*this.data.length;
        d3.select("#animationSVG").remove();

        var svg = d3.select(".color-pallet")
            .append("svg")
            .attr("id", "animationSVG")
            .attr("width", svgWidth)
            .attr("height", svgHeight)

        for(let idx in this.data) {
            let random = Math.floor(Math.random() * this.colorPallet.length);
            let rect =this. data[idx];
            svg.append("rect")
                .attr("id", "rect" + idx)
                .attr("x", rect.x1)
                .attr("y", rect.y1)
                .attr("width", 100)
                .attr("height", 100)
                .attr("fill", (d,i)=> this.colorPallet[random])
                .style("opacity", 0)
                .transition()
                .duration(500)
                .style("opacity", 1)
                .transition()
                .duration(1000)
                .attr("y", rect.y2)
                .attr("x", rect.x2)
        }
    }

    render() {
        return (
            <div className="loading-animation">
                <div className="color-pallet"></div>
            </div>
        );
    }
}


function Home(props) {
  return (
  	<div className="container-fluid">
        <LoadAnimation/>
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div style={{ 'textAlign': 'center', 'marginBottom': `300px` }}>
	    	<button className="btn btn-outline-light">Contact Me</button>
	    </div>
    </div>
  )
}

export default Home;
