import React from 'react'; // Import the Component component from React
import {Link} from 'react-router-dom'; // Import the Link component
import './App.css';

class Content extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="row">
        <div className="col-4">
          <div className="project-title"><h4>{this.props.props.title}</h4></div>
          <title> Tools Used </title>
          <List data={ data }  />
          <ul>
            <li>Coffee</li>
            <li>Tea</li>
            <li>Milk</li>
          </ul>

        </div>
        <div className="col-8">
          <div className="project-image">
            <img src={this.props.props.image} alt="Project Thumbnail"/>
          </div>
          <div className="project-title"><h4>{this.props.props.title}</h4></div>
          <div className="project-category">{this.props.props.category}</div>
        </div>

      </div>
    );
  }
}


var List = React.createClass({
    render: function() {
        return (<div>
        { this.props.data.map(function(item) {
                return <div>{item}</div>
            })
        }
        </div>);
    }
});

var data =  ['red', 'green', 'blue'];

// React.render(, document.body);



function Thumbnail(props) {
  // use a tag if using extenal link
  if(props.externalLink === "true") {
    return (
      <div className="project">
        <a href={props.link}>
          <Content props={props}/>
        </a>
      </div>
    );
  } else {
    return (
      <div className="project">
        <Link to={props.link}>
          <Content props={props}/>
        </Link>
      </div>
    );
  }

}

export default Thumbnail;
