import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};

        // This binding is necessary to make `this` work in the callback
        this.handleClick = this.handleClick.bind(this);
}

    componentDidMount() {
        this.startAnimation()
    }

    handleClick() {
        this.setState(state => ({
            isPaused: !state.isPaused
        }));
        if(this.state) {
            pauseGame();
        }
    }

    render() {
        return (
            <div id="pause-game">
                <button onClick={this.handleClick}
                        className="btn">
                    {this.state.isPaused ? 'pause' : 'resume!'}
                </button>
            </div>
        );
    }
}

function drawColorPallet() {
    let colorPallet = ['#191B58', '#FAA68C', '#3C6997', '#5ADBFF', '#050505'];

    var svg = d3.select(".color-pallet")
        .append("svg")
        .attr("width", 400)
        .attr("height", 200);

    var data = [{x1: 20, x2: 60, y1: 30, y2: 50},
    {x1: 50, x2: 80, y1: 100, y2: 150},
    {x1: 200, x2: 400, y1: 10, y2: 100}];

    var rects = svg.selectAll("foo")
        .data(data)
        .enter()
        .append("rect")
        .attr("x", d=> d.x1)
        .attr("y", d=> d.y1)
        .attr("width", d=> d.x2 - d.x1)
        .attr("height", d=> d.y2 - d.y1)
        .attr("fill", "teal");

    console.warn("rects=", rects)
}
function Home(props) {
  return (
  	<div className="container-fluid">
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div style={{ 'textAlign': 'center', 'marginBottom': `300px` }}>
	    	<button className="btn btn-outline-dark">Contact Me</button>
	    </div>
	    <div className="color-pallet"></div>
    </div>
  )
}

export default Home;
