import React from "react"
import './App.css';
import * as d3 from "d3";

class LoadAnimation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {isPaused: true};
}

    componentDidMount() {
        this.startAnimation()
    }

    startAnimation() {
        let colorPallet = ['#191B58', '#FAA68C', '#3C6997', '#5ADBFF', '#050505'];
        console.warn("DEBUG colorPallet =", colorPallet)
        var svg = d3.select(".color-pallet")
            .append("svg")
            .attr("width", 500)
            .attr("height", 200);

        var data = [
                {x: 0, y: 30},
                {x: 50, y: 30},
                {x: 100, y: 30},
                {x: 150, y: 30},
                {x: 200, y: 30},
            ];

        var rects = svg.selectAll("foo")
            .data(data)
            .enter()
            .append("rect")
            .attr("x", (d,i)=> i*100)
            .attr("y", d=> d.y)
            .attr("width", 100)
            .attr("height", 100)
            .attr("fill", "white")
            .transition()
            .duration(1000)
            .attr("fill", (d,i)=> colorPallet[i]);

        console.warn("rects=", rects)
    }

    render() {
        return (
            <div className="color-pallet"></div>
        );
    }
}


function Home(props) {
  return (
  	<div className="container-fluid">
	    <div className="col-6 home-section">
	      <h1>William Vennard</h1>
	      <h2>Web Developer & Mechanical Engineer</h2>
	    </div>
	    <div style={{ 'textAlign': 'center', 'marginBottom': `300px` }}>
	    	<button className="btn btn-outline-dark">Contact Me</button>
	    </div>
        <LoadAnimation/>

    </div>
  )
}

export default Home;
