    this.spawnFood();
    this.gameTick();
  }

  randomBoardCord() {
    let segDim = this.segmentDim;
    let x = Math.round(Math.random() * this.boardWidth / segDim) * segDim - segDim;
    let y = Math.round(Math.random() * this.boardHeight / segDim) * segDim - segDim; // keep food on map

    if (x > this.boardWidth) {
      x = this.boardWidth - segDim;
    }

    if (x < 0) {
      x = 0;
    }

    if (y > this.boardHeight) {
      y = this.boardHeight - segDim;
    }

    if (y < 0) {
      y = 0;
    }

    return [x, y];
  }

  spawnFood() {
    this.foodCord = this.randomBoardCord();
    d3.select('#board').append('rect').attr("id", "food").attr("width", this.segmentDim).attr("height", this.segmentDim).attr("x", this.foodCord[0]).attr("y", this.foodCord[1]).attr("ry", this.segmentRadius).attr("rx", this.segmentRadius).attr("fill", "limegreen").style("opacity", 0).transition().duration(1000).style("opacity", 1);
    this.foodOnBoard = true;
  }

  moveSnake(index, cord) {
    d3.select('#segment' + index).attr("x", cord[0]).attr("y", cord[1]);

    if (+index !== this.snakeBody.length - 1) {
      let nextIdx = index + 1;
      let oldCord = this.snakeBody[index];
      this.moveSnake(nextIdx, oldCord);
    }

    this.snakeBody[index] = [cord[0], cord[1]];
  }

  gameLost() {
    clearTimeout(this.gameTicker);
    console.log('Game over.');

    if (!this.playing) {
      return;
    }

    this.playing = false; // remove old stuff and show game over

    d3.select('#food').transition().duration(1000).style("opacity", 0).remove();
    d3.selectAll(".snake").transition().duration(1000).style("opacity", 0).remove();
