        this.score++;

        if (this.speed > 100) {
          this.speed = this.speed - 25;
        }

        d3.select("#score-count").text(this.score);
        d3.select('#food').remove();
        this.foodOnBoard = false;
        this.snakeBody.push(this.snakeTail);
        d3.select('#board').append('rect').attr("class", "snake").attr("id", "segment" + (this.snakeBody.length - 1)).attr("width", this.segmentDim).attr("height", this.segmentDim).attr("x", this.snakeTail[0]).attr("y", this.snakeTail[1]).attr("ry", this.segmentRadius).attr("rx", this.segmentRadius).attr("fill", "purple");
      }
    }

    if (this.count % 5 === 0 && !this.foodOnBoard) {
      this.spawnFood();
    } // put bomb stuff in seperate function


    let snakeBombs = new SnakeBombs();
    console.warn("DEBUG DEBUG game tick: snakeBombs =", snakeBombs); // bomb tick

    if (this.liveBomb) {
      console.log('This bomb is ticking...', bombCount); // count down in seconds

      let slowCount = Math.round(1000 / this.speed);

      if (+this.bombCount > 0 && this.count % slowCount === 1) {
        this.bombCount = this.bombCount - 1;
        d3.select('#bomb-text').text(this.bombCount);
      } else if (this.bombCount <= 0) {
        // boom
        console.log("Boom!");
        this.bombGoesBoom();
      }
    } // spawn bomb


    if (this.score > 0 && this.count % 10 === 0 && !this.liveBomb) {
      this.spawnBomb();
    } // losing conditions


    if (this.headCord[0] >= this.boardWidth || this.headCord[0] < 0 // off the sides 
