class SnakeBombs extends SnakeGame {
  constructor() {
    this.bombCord = [];
    this.liveBomb = false;
    this.bombCount = 0;
    this.explosionRadius = 2;
  }

  spawnBomb() {
    this.bombCord = randomBoardCord();
    this.bombCount = 5;
    d3.select('#board').append('rect').attr("id", "bomb").attr("width", this.segmentDim).attr("height", this.segmentDim).attr("x", this.bombCord[0]).attr("y", this.bombCord[1]).attr("ry", this.segmentRadius).attr("rx", this.segmentRadius).attr("fill", "orange").style("opacity", 0).attr("stroke", "red").transition().duration(1000).style("opacity", 1);
    d3.select('#board').append('text').attr('id', 'bomb-text').text(this.bombCount).style('fill', 'black').style('font-size', '65%').attr("transform", "translate(" + (this.bombCord[0] + this.segmentDim / 4) + "," + (this.bombCord[1] + this.segmentDim - 1) + ")");
    this.liveBomb = true;
  }

  bombGoesBoom() {
    let colorScale = d3.scaleLinear().domain([0, this.bombRadius]).range(['red', 'yellow']);
    let explosionArea = [];

    for (let i = 1; i <= this.bombRadius; i++) {
      let start = this.bombCord;

      for (let xDim = -i; xDim <= i; xDim++) {
        for (let yDim = -i; yDim <= i; yDim++) {
          let newX = start[0] + xDim * this.segmentDim;
          let newY = start[1] + yDim * this.segmentDim;
          let newCord = [newX, newY];

          if (!explosionArea.includes(newCord)) {
            explosionArea.push(newCord);
          }
        }
      }
    }

    for (let block of explosionArea) {
      // distance in block units
      let distance = distanceBetweenPoints(this.bombCord, block) / segmentDim;
      d3.select('#board').append('rect').attr("class", "boom").attr("width", this.segmentDim).attr("height", this.segmentDim).attr("x", block[0]).attr("y", block[1]).attr("ry", this.segmentRadius).attr("rx", this.segmentRadius).attr("fill", colorScale(distance)).style("opacity", 1).attr("stroke", "red").transition().duration(1500).style("opacity", 0).attr("fill", "blue").attr("width", 1).attr("height", 1).remove();
    }

    d3.select('#bomb-text').remove();
    d3.select('#bomb').transition().duration(1500).style("opacity", 0).remove();
    this.bombCount = 0;
    this.liveBomb = false;
    this.isHeadInExplosion();
  }

  isHeadInExplosion() {
    let bombR = this.bombRadius;
    let segDim = this.segmentDim;
    let explosionXRange = [this.bombCord[0] - segDim * bombR, this.bombCord[0] + segDim * bombR];
    let explosionYRange = [this.bombCord[1] - segDim * bombR, this.bombCord[1] + segDim * bombR];
    let detectX = this.headCord[0] >= explosionXRange[0] && this.headCord[0] <= explosionXRange[1];
    let detectY = this.headCord[1] >= explosionYRange[0] && this.headCord[1] <= explosionYRange[1];

    if (detectX && detectY) {
      console.log("Ouch, killed by a bomb...");
      this.gameLost();
    }
  }

}

function distanceBetweenPoints(pointA, pointB) {
  let x0 = pointA[0];
  let x1 = pointB[0];
  let y0 = pointA[1];
  let y1 = pointB[1];
  let x = Math.pow(x1 - x0, 2);
  let y = Math.pow(y1 - y0, 2);
  let distance = Math.pow(x + y, 0.5);
  return distance;
}

